var mapLoader = function(id, x, y, xx, yy, layer, sceneName){
    var scene = WIZARD.scene.scenes[sceneName];
    var contador = 0;
    if(!scene.collisions) scene.collisions = [];
    if(!scene.collisions[y]) scene.collisions[y] = [];
    scene.collisions[y][x] = 0;

    if(id != -1){
        scene.collisions[y][x] = 1;
        if(id == 1) { //Collision
            scene.collisions[y][x] = 1;
        }else if(id == 2) { //House To Town
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "town", sceneX: 8, sceneY: 10, xx, yy}, sceneName);
        }else if(id == 3) { //Town To House
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "house", sceneX: 4, sceneY: 8, xx, yy}, sceneName);
        }else if(id == 4) { //Town to Road Ins
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "road_ins", sceneX: 2, sceneY: 10, xx, yy}, sceneName);
        }else if(id == 5) { //Town to Road Lake
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "road_lake", sceneX: 7, sceneY: 3, xx, yy}, sceneName);
        }else if(id == 6) { //Road Lake to Town
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "town", sceneX: 14, sceneY: 21, xx, yy}, sceneName);
        }else if(id == 7) { //Road Ins to Town
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "town", sceneX: 23, sceneY: 12, xx, yy}, sceneName);
        }else if(id == 8) { //Ins to Road Lake
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "road_lake", sceneX: 38, sceneY: 3, xx, yy}, sceneName);
        }else if(id == 9) { //Road Lake to Ins
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "institute", sceneX: 8, sceneY: 18, xx, yy}, sceneName);
        }else if(id == 10) { //Road Ins to Ins
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "institute", sceneX: 1, sceneY: 3, xx, yy}, sceneName);
        }else if(id == 11) { //Road Lake To lake
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "lake", sceneX: 12, sceneY: 2, xx, yy}, sceneName);
        }else if(id == 12) { //Road Lake To forest
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "forest", sceneX: 9, sceneY: 17, xx, yy}, sceneName);
        }else if(id == 13) { //Ins to Road Ins
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "road_ins", sceneX: 11, sceneY: 10, xx, yy}, sceneName);
        }else if(id == 14) { //Lake to road lake
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "road_lake", sceneX: 23, sceneY: 24, xx, yy}, sceneName);
        }else if(id == 15) { //Forest to road lake
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("portal", {x: x * 16, y: y * 16, scene: "road_lake", sceneX: 23, sceneY: 17, xx, yy}, sceneName);
        }else if(id == 16) { // Player
                scene.collisions[y][x] = 0;
                player = WIZARD.entity.instantiateToScene("player", {x: x * 16, y: y * 16}, sceneName);
        }else if(id == 17) { // TEST_APPLE
            WIZARD.entity.instantiateToScene("object", {x: x * 16, y: y * 16, xx: xx, yy: yy, type: ENTITY_TYPE.TEST_APPLE}, sceneName);
        }else if(id == 18){ // GIRL
            WIZARD.entity.instantiateToScene("npc", {x: x * 16, y: y * 16, type: ENTITY_TYPE.GIRL}, sceneName);
        }else if(id == 19){// TEST_BOOK
            WIZARD.entity.instantiateToScene("objectObservable", {x: x * 16, y: y * 16, type: ENTITY_TYPE.TEST_BOOK}, sceneName);
        }else if(id == 20){// DOG_LAKE
            WIZARD.entity.instantiateToScene("npc", {x: x * 16, y: y * 16, type: ENTITY_TYPE.DOG_LAKE}, sceneName);
        }else if(id == 56 || id == 72){// FOOTSTEPS
            scene.collisions[y][x] = 0;
            WIZARD.entity.instantiateToScene("objectObservable", {x: x * 16, y: y * 16, xx: xx, yy: yy, type: ENTITY_TYPE.FOOTSTEPS}, sceneName);
        }else if(id == 89){// Entidad puzzle2
            WIZARD.entity.instantiateToScene("object", {x: x * 16, y: y * 16, xx: xx, yy: yy, type: ENTITY_TYPE.TEST_SIGNBOARD}, sceneName);
        }else{
            scene.collisions[y][x] = 0;
            if(!scene.tiles) scene.tiles = [];
            if(!scene.tiles["layer" + layer]) scene.tiles["layer" + layer] = [];
            var tiles = scene.tiles["layer" + layer];
            WIZARD.entity.instantiateToList("tile", {x: x * 16, y: y * 16, xx, yy}, tiles);
        }
    }
};

var isFreeTile = function(x, y){
    var free =  x > 0 && x < WIZARD.scene.current.collisions[0].length &&
            y > 0 && y < WIZARD.scene.current.collisions.length &&
            WIZARD.scene.current.collisions[y][x] == 0;
    return free;
};
